import { addToCart } from "../Actions/cartAction";
import AddIcon from "@material-ui/icons/Add";
import {
  Button,
  CardActions,
  CardHeader,
  CardMedia,
  Card,
  Typography
} from "@material-ui/core";

const styles = theme => ({
  root: {
    width: 345,
    margin: 10
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  }
})
class ProductCard extends React.Component{
