    }
  }
  handleAddtoCart = () => {
    this.props.addToCart({
      name: this.props.name,
      price: this.props.price,
      image: this.props.image
    })
  }

  render(){
    const {classes} = this.props;
    let location = "Location: "+this.props.locat
    return (
      <Card className={classes.root}>
        <CardHeader title={this.props.name} subheader={location}/>
        <CardMedia
          className={classes.media}
          image={this.props.image[0]}
        />
        <CardActions style={{ display: "flex", justifyContent: "space-between" }}>
          <Typography variant="body1" gutterBottom align="center">
            â‚¹ {this.props.price}
          </Typography>
          <Button
            variant="outlined"
            size="small"
            color="primary"
            className={classes.button}
            startIcon={<AddIcon />}
            onClick={this.handleAddtoCart}>
            Add to cart
          </Button>
        </CardActions>
      </Card>
    )
  }
}
const mapStateToProps = state => {
  const { cart } = state
  return ({ cart })
};
export default connect( mapStateToProps,{ addToCart })(withRouter(withStyles(styles)(ProductCard)));
