import React from "react";
import '../App.css';
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { addToCart } from "../Actions/cartAction";
import { Box, Typography } from "@material-ui/core";
class ProductDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mainImage: this.props.location.state.image[0],
        }
    }
    handleSetMainImage = (image) => {
        this.setState({ mainImage: image })
    }
    render() {
        console.log("product: ",this.props.location.state)
        return (
            <Box className='product-detail-container'>
                <Box className='product-box'>
                    <Box className='image-list'>
                        { this.props.location.state.image.map(image =><Box className='image-list-item' onMouseOver={()=>this.handleSetMainImage(image)}><img src={image} style={{height:80, width:'auto'}}/></Box>)}
                    </Box>
                    <Box className='image-main'>
                        <img src={this.state.mainImage} />
                    </Box>
                    <Box className='product-description'>
                    <Typography variant='h3'>{this.props.location.state.price}</Typography>
                        <Typography variant='body 2'>{this.props.location.state.description}</Typography>
                    </Box>
                </Box>
                <Box className='comment-box'>

                </Box>
            </Box>
        )
    }
}
const mapStateToProps = state => {
    const { cart } = state
    return ({ cart })
};
export default connect(mapStateToProps, { addToCart })(withRouter(ProductDetail));
